import { Component, OnInit } from '@angular/core';
import { ModalidadService } from 'src/app/services/modalidad.service';
import { DistritoService } from 'src/app/services/distrito.service';
import { HorarioService } from 'src/app/services/horario.service';
import { ServicioService } from 'src/app/services/servicio.service';
import { TarifaService } from 'src/app/services/tarifa.service';
import { ClienteService } from 'src/app/services/cliente.service';

@Component({
  selector: 'app-servicios',
  templateUrl: './servicios.component.html',
  styleUrls: ['./servicios.component.scss']
})
export class ServiciosComponent implements OnInit {
  servicios: any[] = [];
  //programasInstalacion: any[] = [];
  //programasReparacion: any[] = [];
  modalidades: any[] = [];
  distritos: any[] = [];
  horarios: any[] = [];
  tarifasDistritos: any[] = [];
  cliente: any = {};
  nombreCompleto: string = '';

  servicio: any = {
    descripcion_servicio: '',
    modalidad: '',
    distrito: '',
    direccion: '',
    fecha: '',
    hora: '',
    id_cliente: this.cliente.id_cliente,
    dni: '',
    comentario: '',
    precio: 0.00,
  };
  

idServicioSeleccionado: any = '';
precioSeleccionado: number = 0.00;
fechaMinima: string = '';



  constructor(
    private modalidadService: ModalidadService,
    private distritoService: DistritoService,
    private horarioService: HorarioService,
    private servicioService: ServicioService,
    private tarifaService: TarifaService,
    private clienteService: ClienteService
  ) { }

  ngOnInit(): void {

    const hoy = new Date();
  // Crear fecha de mañana
  const manana = new Date(hoy);
  manana.setDate(hoy.getDate() + 1);

  // Convertir a formato YYYY-MM-DD
  this.fechaMinima = manana.toISOString().split('T')[0];

  // Setear valor inicial del campo fecha
  this.servicio.fecha = this.fechaMinima;
    // Cargar modalidades
    this.modalidadService.getModalidades().subscribe({
      next: (d: any[]) => this.modalidades = d,
      error: (err: any) => console.error('Error al cargar modalidades:', err)
    });

    // Cargar distritos
    this.distritoService.listar().subscribe({
      next: (d: any[]) => this.distritos = d,
      error: (err: any) => console.error('Error al cargar distritos:', err)
    });

    // Cargar horarios
    this.horarioService.listar().subscribe({
      next: (h: any[]) => this.horarios = h,
      error: (err: any) => console.error('Error al cargar horarios:', err)
    });

    // Cargar tarifas por distrito
    this.tarifaService.listar().subscribe({
      next: (t: any[]) => this.tarifasDistritos = t,
      error: (err: any) => console.error('Error al cargar tarifas:', err)
    });
    
    this.servicioService.getServiciosInstalacion().subscribe({
      next: (s: any[]) => this.servicios = s,
      error: (err: any) => console.error('Error al cargar instalación:', err)
    });


    // // Cargar programas de reparación
    // this.servicioService.getServiciosReparacion().subscribe({
    //   next: (d: any[]) => this.programasReparacion = d,
    //   error: (err: any) => console.error('Error al cargar reparación:', err)
    // });

    const token = localStorage.getItem('token');
    const idCliente = Number(localStorage.getItem('id_cliente'));

    if (token && idCliente) {
      this.clienteService.obtenerCliente(idCliente, token).subscribe({
        next: data => {
          this.cliente = data;
          this.nombreCompleto = `${this.cliente.nombres} ${this.cliente.apellidos}`;
          this.servicio.id_cliente = this.cliente.id_cliente;
        },
        error: err => console.error('Error al cargar cliente:', err)
      });
    }
  }

  // actualizarPrecio(): void {
  //   // Precio base del servicio
  //   const servicio = [...this.programasInstalacion, ...this.programasReparacion]
  //     .find(s => s.id_servicio === this.servicio.programa);

  //   // Tarifa adicional por distrito
  //   const tarifa = this.tarifasDistritos.find(t => t.id_distrito === this.servicio.distrito);

  //   const precioServicio = servicio ? servicio.precio : 0;
  //   const precioDistrito = tarifa ? tarifa.precio : 0;

  //   this.servicio.precio = precioServicio + precioDistrito;
  // }
  // setearPrecio(idServicio: any): void {
  //   //console.log(this.servicios.id_servicio);
  //    if (idServicio) {
  //     const servicioSeleccionado = this.servicios.find(
  //     (servicio: {idServicio: Number}) => servicio.id_servicio == idServicio
  //   );
  //   console.log('Servicio seleccionado:', servicioSeleccionado);

  //   this.servicio.precio = servicioSeleccionado ? servicioSeleccionado.precio : 0;
  //   console.log('Precio actualizado:', this.servicio.precio);
      
  //    }
  //    this.servicio.precio = 0;
  //      console.log('Precio cero:', this.servicio.precio);
  //      return;
    
  // }
  setearPrecio(): void {
  const servicio = this.servicios.find(s => s.id_servicio == this.idServicioSeleccionado);
  console.log('Servicio seleccionado:', servicio);
  this.precioSeleccionado = servicio ? parseFloat(servicio.precio) : 0.00;
  console.log('Precio actualizado:', this.precioSeleccionado);
}

validarModalidad() {
  if (this.servicio.modalidad != 3) {
    this.servicio.distrito = '';
    this.servicio.direccion = '';
  }
}

get precioServicio(): string {
  const n = Number(this.precioSeleccionado) || 0;
  return n.toFixed(2); // o n.toLocaleString('es-PE', {minimumFractionDigits:2})
}


  registrarServicio(): void {
    if (!this.formularioValido()) {
      alert('⚠️ Completa todos los campos obligatorios antes de registrar el servicio.');
      return;
    }

    this.servicioService.registrarServicio(this.servicio).subscribe({
      next: () => alert('✅ Servicio registrado correctamente'),
      error: (err: any) => console.error('Error al registrar servicio:', err)
    });
  }

  formularioValido(): boolean {
    return this.servicio.programa && this.servicio.distrito && this.servicio.precio > 0;
  }
}
