import { Component } from '@angular/core';

type Mensaje = { rol: 'bot' | 'user'; texto: string };

@Component({
  selector: 'app-inicio',
  templateUrl: './inicio.component.html',
  styleUrls: ['./inicio.component.scss']
})
export class InicioComponent {
  chatAbierto = false;
  entrada = '';
  mensajes: Mensaje[] = [
    { rol: 'bot', texto: 'Hola üëã Soy tu asistente. ¬øEn qu√© puedo ayudarte hoy?' }
  ];
  escribiendo = false;
  toggleChat(): void {
    this.chatAbierto = !this.chatAbierto;
  }

  enviarPregunta(texto: string): void {
    this.mensajes.push({ rol: 'user', texto });
    this.escribiendo = true;
    const respuesta = this.responder(texto);
    setTimeout(() => {
      this.mensajes.push({ rol: 'bot', texto: respuesta });
      this.escribiendo = false;
    }, 1200); // efecto "escribiendo..."
  }

  onSubmit(event: Event): void {
    event.preventDefault();
    const texto = this.entrada.trim();
    if (!texto) return;
    this.enviarPregunta(texto);
    this.entrada = '';
  }

 private responder(texto: string): string {
  const t = texto.toLowerCase();

  // Registro
    if (t.includes('registrar') || t.includes('crear cuenta') || t.includes('registro')) {
      return 'Para registrarte, haz clic en "Registrar cuenta" en la parte superior derecha. Llena tus datos y crea tu contrase√±a segura. Luego podr√°s acceder a todos nuestros servicios.';
    }

    // Inicio de sesi√≥n
    if (t.includes('iniciar') || t.includes('login') || t.includes('sesion')) {
      return 'Para iniciar sesi√≥n, usa el bot√≥n "Iniciar sesi√≥n" en la esquina superior derecha. Ingresa tu correo y contrase√±a. Si olvidaste tu contrase√±a, hay un enlace para recuperarla debajo del formulario.';
    }

    // Recuperar contrase√±a
    if (t.includes('olvid√©') || t.includes('contrase√±a')) {
      return 'No te preocupes üôå. Haz clic en "¬øOlvidaste tu contrase√±a?" en la pantalla de inicio de sesi√≥n y sigue las instrucciones para recuperarla.';
    }

    // Agendar cita
    if (t.includes('cita') || t.includes('agendar') || t.includes('agenda')) {
      return 'Para agendar una cita, primero inicia sesi√≥n. Luego entra a la secci√≥n "Citas", selecciona el servicio que necesitas (diagn√≥stico, instalaci√≥n, soporte) y elige fecha y hora disponibles.';
    }

    // Tarifas
    if (t.includes('tarifa') || t.includes('precio') || t.includes('costo')) {
      return 'Las tarifas dependen del servicio y modalidad (remoto, a domicilio, en oficina o express). Una vez que inicies sesi√≥n, podr√°s ver el detalle de precios seg√∫n tu distrito y el tipo de atenci√≥n que elijas.';
    }

    // Modalidades de atenci√≥n
    if (t.includes('modalidad') || t.includes('tipo de atenci√≥n') || t.includes('c√≥mo atienden')) {
      return 'Tenemos varias modalidades de atenci√≥n: \n\n' +
             'üëâ Remoto: soporte en l√≠nea para incidencias de software.\n' +
             'üëâ A domicilio: un t√©cnico visita tu distrito, ideal para hardware o instalaciones.\n' +
             'üëâ En oficina: puedes traer tu equipo a nuestro taller.\n' +
             'üëâ Express: atenci√≥n prioritaria con tiempos m√°s r√°pidos.\n\n' +
             'Inicia sesi√≥n para elegir la modalidad que prefieras al agendar tu cita.';
    }

    // Servicios disponibles
    if (t.includes('servicio') || t.includes('soporte') || t.includes('instalaci√≥n') || t.includes('diagn√≥stico')) {
      return 'Ofrecemos diagn√≥stico de equipos, instalaci√≥n de software, soporte remoto y soporte a domicilio. Inicia sesi√≥n para ver tus servicios activos y solicitar nuevos.';
    }

    // Orientaci√≥n general
    if (t.includes('no s√©') || t.includes('ayuda') || t.includes('nuevo')) {
      return 'Tranquilo, estoy aqu√≠ para guiarte üíô. Puedes registrarte si a√∫n no tienes cuenta, iniciar sesi√≥n si ya la creaste, y desde tu panel acceder a citas, tarifas y servicios. ¬øQuieres que te muestre c√≥mo empezar paso a paso?';
    }

    // Respuesta por defecto
    return 'Puedo ayudarte con registro, inicio de sesi√≥n, recuperar contrase√±a, agendar citas, tarifas, modalidades y servicios. ¬øSobre qu√© quieres saber m√°s ahora?';
  }




  
}
