//     next: (resp: any) => {
//       if (resp.success) {
//         // Guardar token
//         localStorage.setItem('token', resp.token);
        
//         // Guardar el id_cliente real
//         localStorage.setItem('id_cliente', resp.id_cliente);
//         this.mensajeExito = '✅ Login exitoso';
//           this.mensajeError = '';
//           this.router.navigate(['/cliente/dashboard']);
//       }
//     },
//     error: (err) => {
//       console.error("Error login:", err);
//       this.mensajeError = '❌ Credenciales inválidas';
//       this.mensajeExito = '';
//     }
//   });
// }

  // Obtener rol actual
  getRole(): string | null {
    return this.role || localStorage.getItem('rol');
  }
  
  obtenerDatosCliente(id: number): Observable<any> {
    const token = this.getToken();
    if (!token) {
      this.router.navigate(['/login']);
      throw new Error("Usuario no autenticado");
    }
    return this.clienteService.obtenerCliente(id, token);
  }

  // Verificar si está logueado
  isLoggedIn(): boolean {
    return !!this.getRole();
  }

  // Obtener token
  getToken(): string | null {
    return localStorage.getItem('token');
  }

  // Redirección según rol (siempre devuelve string)
  redirectHomeByRole(): string {
    const rol = this.getRole();
    if (rol === 'cliente') {
      this.router.navigate(['/inicio']);
      return '/inicio';
    }
    if (rol === 'admin') {
      this.router.navigate(['/admin']);
      return '/admin';
    }
    if (rol === 'tecnico') {
      this.router.navigate(['/tecnico']);
      return '/tecnico';
    }
    this.router.navigate(['/login']);
    return '/login';
  }

  // Cerrar sesión
  logout() {
    this.role = null;
    localStorage.clear();
    this.router.navigate(['/login']);
  }
}
